import { gql, useQuery } from "@apollo/client";
import Card from "./components/Card";

const GET_TODOS = gql`
  query getSales {
    sales {
      id
      price
    }
  }
`;

export const App = () => {
  const { data, loading, error } = useQuery(GET_TODOS);

  return (
    <div>
      {data?.sales?.map(({ price, id }: any) => (
        <Card key={id} price={price / 24} />
      ))}
    </div>
  );
};

export default App;
